import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

const AdminFarm = () => {
    const location = useLocation();
    const { farmerId, admin_id } = location.state || {};
    const [farmerDetails, setFarmerDetails] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if (farmerId && admin_id) {
            const fetchFarmerDetails = async () => {
                try {
                    const response = await fetch('/admin/farmer', {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json'
                        },
                        body: JSON.stringify({
                            admin_id: admin_id,
                            farmer_id: farmerId
                        })
                    });
                    const data = await response.json();
                    setFarmerDetails(data);
                    setLoading(false);
                } catch (error) {
                    console.error('Error fetching farmer details:', error);
                    setLoading(false);
                }
            };
            fetchFarmerDetails();
        }
    }, [farmerId, admin_id]);

    if (loading) {
        return (
            <div className="d-flex justify-content-center align-items-center" style={{ height: '100vh' }}>
                <div className="spinner-border" role="status">
                    <span className="visually-hidden">Loading...</span>
                </div>
            </div>
        );
    }

    if (!farmerDetails) {
        return <div>No farmer details found.</div>;
    }

    return (


<div>
    <div className='container my-5'>
        <div className='borderring gridSystem'>
            <div className='farmer_details borderring'>
                Farmer Details
            </div>
            <div className='farm-and-section borderring'>
                Farm and Section Details
            </div>
        </div>
    </div>
</div>




    );
};

export default AdminFarm;
