import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, LineElement } from 'chart.js';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Popover, OverlayTrigger } from 'react-bootstrap';
import Modal from './Modal';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, LineElement);

const Sections = ({ sectionsData = [] }) => {
    const [showModal, setShowModal] = useState(false);

    const handleShowModal = () => setShowModal(true);
    const handleCloseModal = () => setShowModal(false);

    const columnChartData = {
        labels: sectionsData.map(section => section.section_name),
        datasets: [
            {
                label: 'Moisture Value',
                data: sectionsData.map(section => Math.floor(section.avg_section_moisture)),
                backgroundColor: 'darkgreen',
                borderColor: 'black',
                borderWidth: 1,
                type: 'bar',
            },
            {
                label: 'Moisture Value Line',
                data: sectionsData.map(section => Math.floor(section.avg_section_moisture)),
                borderColor: 'black',
                backgroundColor: 'transparent',
                borderWidth: 2,
                fill: false,
                type: 'line',
                tension: 0.4,
            },
        ],
    };

    return (
        <div>
            <div className="container p-0">
                <div className="row">
                    <div className="col-12 col-sm-6">
                        <h6>Sections</h6>
                        {/* Section details and control buttons here */}
                    </div>
                    <div className="col-12 col-sm-6">
                        <div>
                            <div className="d-flex align-items-center justify-content-between mb-1">
                                <span>Section Moisture Chart</span>
                                <small
                                    className="text-secondary"
                                    style={{ cursor: 'pointer' }}
                                    onClick={handleShowModal}
                                >
                                    More charts
                                </small>
                            </div>
                            <div style={{ height: '100%' }}>
                                <Bar data={columnChartData} options={{ responsive: true }} />
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Pass chart data and types to Modal */}
            <Modal
                show={showModal}
                handleClose={handleCloseModal}
                chartData={columnChartData}
                chartTypes={['line', 'bar']}
            />
        </div>
    );
};

export default Sections;
