import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const UserData = () => {
  const { id } = useParams();
  const [rec, setRec] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`/user/${id}`)
      .then(response => response.json())
      .then(data => {
        setRec(data);
        console.log(data);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, [id]);

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      <h2>Farmer Details</h2>
      {rec?.farmer_details && (
        <div>
          <p>Name: {rec.farmer_details.farmer_fname}</p>
          <p>ID: {rec.farmer_details.farmer_id}</p>
        </div>
      )}
      <h3>Farm Details</h3>
      {rec?.farm_details?.map(farm => (
        <div key={farm.farm_id}>
          <p>Farm Name: {farm.farm_name}</p>
          <p>Farm Size: {farm.farm_size}</p>
        </div>
      ))}
      <h3>Total Farms: {rec?.total_farm_count}</h3>
    </div>
  );
};

export default UserData;
