import React, { useState, useEffect, useRef } from 'react';
import { Bar, Line, Radar, Pie, Doughnut, PolarArea } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, PointElement, LineElement, ArcElement } from 'chart.js';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Modal } from 'react-bootstrap';

ChartJS.register(
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend,
    PointElement,
    LineElement,
    ArcElement
);

const Sections = ({ sectionsData = [] }) => {
    const [sectionsState, setSectionsState] = useState(sectionsData);
    const [showModal, setShowModal] = useState(false);
    const chartRef = useRef([]);

    const columnChartData = {
        labels: sectionsState.map(section => section.section_name),
        datasets: [
            {
                label: 'Moisture Value',
                data: sectionsState.map(section => Math.floor(section.avg_section_moisture)),
                backgroundColor: 'darkgreen',
                borderColor: 'black',
                borderWidth: 1,
                type: 'bar', // Bar chart
            },
            {
                label: 'Moisture Value Line',
                data: sectionsState.map(section => Math.floor(section.avg_section_moisture)),
                borderColor: 'black',
                backgroundColor: 'transparent',
                borderWidth: 2,
                fill: false,
                type: 'line', // Line chart
                tension: 0.4, // For curved lines
            },
        ],
    };

    useEffect(() => {
        // Destroy the previous chart instances to avoid reusing the canvas
        chartRef.current.forEach(chart => {
            if (chart) {
                chart.destroy();
            }
        });
    }, [sectionsState]); // Runs when `sectionsState` changes

    const handleMoreChartsClick = () => setShowModal(true);
    const handleClose = () => setShowModal(false);

    return (
        <div>
            <div className="container p-0">
                <div className="row">
                    <div className="col-12 col-sm-6 d-flex align-items-stretch">
                        <div className="borderring w-100">
                            <h6 className="text-secondary p-1">Sections</h6>
                            <div className="sectionlogs overflow-auto" style={{ maxHeight: '220px' }}>
                                <table className="table table-striped table-hover table-responsive">
                                    <thead>
                                        <tr>
                                            <th scope="col">Section</th>
                                            <th scope="col">Moisture</th>
                                            <th scope="col">Status</th>
                                            <th scope="col">Controls</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {sectionsState.map((section, index) => (
                                            <tr key={section.section_device_id}>
                                                <td>{section.section_name}</td>
                                                <td>{Math.floor(section.avg_section_moisture)}</td>
                                                <td>{section.valve_mode} {section.valve_status}</td>
                                                <td>
                                                    <button type="button" className="btn btn-outline-secondary btn-sm">Auto</button>
                                                    <button type="button" className="btn btn-outline-secondary btn-sm">Manual</button>
                                                </td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                    <div className="col-12 col-sm-6 d-flex align-items-stretch">
                        <div className="borderring w-100">
                            <div className="d-flex align-items-center justify-content-between mb-1">
                                <span>Section Moisture Chart</span>
                                <small
                                    className="text-secondary"
                                    onClick={handleMoreChartsClick}
                                    style={{ cursor: 'pointer', textDecoration: 'none' }}
                                >
                                    More charts
                                </small>
                            </div>
                            <Bar
                                ref={el => chartRef.current[0] = el?.chartInstance}
                                data={columnChartData}
                                options={{ responsive: true }}
                            />
                        </div>
                    </div>
                </div>
            </div>

            {/* Modal for more charts */}
            <Modal show={showModal} onHide={handleClose} size="lg">
                <Modal.Header closeButton>
                    <Modal.Title>Section Moisture Data</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <div className="d-flex flex-column gap-3">
                        {/* Bar Chart */}
                        <div>
                            <h5>Bar Chart</h5>
                            <Bar ref={el => chartRef.current[1] = el?.chartInstance} data={columnChartData} options={{ responsive: true }} />
                        </div>

                        {/* Line Chart */}
                        <div>
                            <h5>Line Chart</h5>
                            <Line ref={el => chartRef.current[2] = el?.chartInstance} data={columnChartData} options={{ responsive: true }} />
                        </div>

                        {/* Radar Chart */}
                        <div>
                            <h5>Radar Chart</h5>
                            <Radar ref={el => chartRef.current[3] = el?.chartInstance} data={columnChartData} options={{ responsive: true }} />
                        </div>

                        {/* Pie Chart */}
                        <div>
                            <h5>Pie Chart</h5>
                            <Pie ref={el => chartRef.current[4] = el?.chartInstance} data={columnChartData} options={{ responsive: true }} />
                        </div>

                        {/* Doughnut Chart */}
                        <div>
                            <h5>Doughnut Chart</h5>
                            <Doughnut ref={el => chartRef.current[5] = el?.chartInstance} data={columnChartData} options={{ responsive: true }} />
                        </div>

                        {/* Polar Area Chart */}
                        <div>
                            <h5>Polar Area Chart</h5>
                            <PolarArea ref={el => chartRef.current[6] = el?.chartInstance} data={columnChartData} options={{ responsive: true }} />
                        </div>
                    </div>
                </Modal.Body>
            </Modal>
        </div>
    );
};

export default Sections;
