import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

const AdminFarm = () => {
    const location = useLocation();
    const { farmerId, admin_id } = location.state || {};
    const [farmerDetails, setFarmerDetails] = useState(null);
    const [farmDetails, setFarmDetails] = useState([]); // State for farm details
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if (farmerId && admin_id) {
            const fetchDetails = async () => {
                try {
                    const response = await fetch('/admin/farmer', {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json'
                        },
                        body: JSON.stringify({
                            admin_id: admin_id,
                            farmer_id: farmerId
                        })
                    });
                    const data = await response.json();
                    setFarmerDetails(data.farmer_data); // Assuming the response has farmer_data
                    setFarmDetails(data.farm_data); // Assuming the response has farm_data
                    setLoading(false);
                } catch (error) {
                    console.error('Error fetching details:', error);
                    setLoading(false);
                }
            };
            fetchDetails();
        }
    }, [farmerId, admin_id]);

    if (loading) {
        return (
            <div className="d-flex justify-content-center align-items-center" style={{ height: '100vh' }}>
                <div className="spinner-border" role="status">
                    <span className="visually-hidden">Loading...</span>
                </div>
            </div>
        );
    }

    if (!farmerDetails) {
        return <div>No farmer details found.</div>;
    }

    return (
        <div className='container my-5'>
            <div className='borderring flexContainer'>
                <div className='farmer_details borderring'>
                    <h2>Farmer Details</h2>
                    <p>ID: {farmerDetails.farmer_id}</p>
                    <p>Name: {farmerDetails.farmer_fname} {farmerDetails.farmer_lname}</p>
                    <p>Phone: {farmerDetails.farmer_phone}</p>
                    <p>Email: {farmerDetails.farmer_email}</p>
                    <p>Join Date: {new Date(farmerDetails.join_date).toLocaleDateString()}</p>
                    <p>Total Farms: {farmerDetails.total_farms}</p>
                </div>
                <div className='farms-farmer borderring'>
                    <h2>Farm and Section Details</h2>
                    {farmDetails.map((farm, index) => (
                        <div key={index} className='farm-section'>
                            <h3>Farm Name: {farm.farm_name}</h3>
                            <p>Farm Size: {farm.farm_size} acres</p>
                            <p>Creation Date: {new Date(farm.creation_date).toLocaleDateString()}</p>
                            <p>Auto On Threshold: {farm.auto_on_threshold}</p>
                            <p>Auto Off Threshold: {farm.auto_off_threshold}</p>
                            <p>Location Coordinates:</p>
                            <ul>
                                {farm.farm_location_cordinates.map((coord, idx) => (
                                    <li key={idx}>{coord}</li>
                                ))}
                            </ul>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default AdminFarm;
