import React, { useState, useEffect } from 'react';
import { OverlayTrigger, Popover } from 'react-bootstrap';
import clouds from './WeatherImages/clouds.png';
import clear from './WeatherImages/clear.png';
import drizzle from './WeatherImages/drizzle.png';
import rain from './WeatherImages/rain.png';
import mist from './WeatherImages/mist.png';

const apikey = "8925502a781648f4443f9e01d96c7ff5";
const apiURL = "https://api.openweathermap.org/data/2.5/forecast?units=metric&lat=";

const FieldControl = ({ collected_data, farmer_details = [] }) => {
  const [threshold1, setThreshold1] = useState(30);
  const [threshold2, setThreshold2] = useState(30);
  const [isEditingThreshold1, setIsEditingThreshold1] = useState(false);
  const [isEditingThreshold2, setIsEditingThreshold2] = useState(false);
  const [latitude, longitude] = collected_data?.location_coordinates?.farm_device?.[0]?.device_location
    ? collected_data.location_coordinates.farm_device[0].device_location.split(", ").map(Number)
    : [null, null];
  const [forecastData, setForecastData] = useState(null);

  const auto_on_threshold = collected_data?.farm_details.auto_on_threshold;
  const auto_off_threshold = collected_data?.farm_details.auto_off_threshold;
  const farm_id = collected_data?.farm_details.farm_details;
  const farmer_id = farmer_details.farmer_id;

  useEffect(() => {
    if (collected_data.farm_details) {
      const { auto_on_threshold, auto_off_threshold } = collected_data.farm_details;
      setThreshold1(auto_on_threshold || 30);
      setThreshold2(auto_off_threshold || 30);
    }
  }, [collected_data]);

  useEffect(() => {
    if (latitude && longitude) {
      fetch(`${apiURL}${latitude}&lon=${longitude}&appid=${apikey}`)
        .then(response => response.json())
        .then(data => {
          const forecast = data.list[2];
          setForecastData(forecast);
        })
        .catch(error => console.error("Error fetching weather forecast data:", error));
    }
  }, [latitude, longitude]);

  const update_threshold = async () => {
    try {
      const originalOnThreshold = collected_data.farm_details.auto_on_threshold;
      const originalOffThreshold = collected_data.farm_details.auto_off_threshold;

      const payload = { farmer_id };

      if (threshold1 !== originalOnThreshold) {
        payload.auto_on_threshold = threshold1;
      }
      if (threshold2 !== originalOffThreshold) {
        payload.auto_off_threshold = threshold2;
      }

      if (!payload.auto_on_threshold && !payload.auto_off_threshold) {
        alert('No changes to update.');
        return;
      }

      const response = await fetch(`/farmer/farm/auto_threshold/${farm_id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      if (response.ok) {
        const result = await response.json();
        console.log('Threshold values updated:', result);
        alert('Threshold values updated successfully!');
      } else {
        const errorText = await response.text();
        console.error('Failed to update thresholds:', errorText);
        alert('Failed to update threshold values.');
      }
    } catch (error) {
      console.error('Error updating thresholds:', error);
      alert('An error occurred while updating thresholds.');
    }
  };

  const handleThresholdChange1 = (e) => setThreshold1(Number(e.target.value));
  const handleThresholdChange2 = (e) => setThreshold2(Number(e.target.value));
  const toggleEditThreshold1 = () => setIsEditingThreshold1(!isEditingThreshold1);
  const toggleEditThreshold2 = () => setIsEditingThreshold2(!isEditingThreshold2);

  const weatherIcons = {
    Clouds: clouds,
    Clear: clear,
    Drizzle: drizzle,
    Rain: rain,
    Mist: mist,
  };

  const upperCase = (name) => name.charAt(0).toUpperCase() + name.slice(1).toLowerCase();

  return (
    <div>
      <hr className="my-1" />
      <div className="controller">
        <div className="container">
          <div className="row pt-2 pb-1">
            <div className="col-12 col-sm-3">
              <h6 className="text-secondary">Threshold</h6>
              <div className="d-flex align-items-center">
                <small>Auto on threshold: </small>
                {isEditingThreshold1 ? (
                  <>
                    <input
                      type="number"
                      value={threshold1}
                      onChange={handleThresholdChange1}
                      className="form-control form-control-sm ms-2"
                      style={{ width: '60px' }}
                      min={10}
                      max={30}
                    />
                    <button
                      onClick={() => {
                        toggleEditThreshold1();
                        update_threshold();
                      }}
                      className="btn btn-sm btn-outline-secondary ms-2"
                    >
                      Save
                    </button>
                  </>
                ) : (
                  <>
                    <small>{threshold1}</small>
                    <button
                      onClick={toggleEditThreshold1}
                      className="btn btn-sm ms-1 text-secondary"
                    >
                      <i className="fa-solid fa-pen"></i>
                    </button>
                  </>
                )}
              </div>
              <div className="d-flex align-items-center">
                <small>Auto off threshold: </small>
                {isEditingThreshold2 ? (
                  <>
                    <input
                      type="number"
                      value={threshold2}
                      onChange={handleThresholdChange2}
                      className="form-control form-control-sm ms-2"
                      style={{ width: '60px' }}
                    />
                    <button
                      onClick={() => {
                        toggleEditThreshold2();
                        update_threshold();
                      }}
                      className="btn btn-sm btn-outline-secondary ms-1"
                    >
                      Save
                    </button>
                  </>
                ) : (
                  <>
                    <small>{threshold2}</small>
                    <button
                      onClick={toggleEditThreshold2}
                      className="btn btn-sm ms-2 text-secondary"
                    >
                      <i className="fa-solid fa-pen"></i>
                    </button>
                  </>
                )}
              </div>
            </div>
            <div className="col-12 col-sm-3">
              <h6 className="text-secondary">Weather in 6 hrs</h6>
              {forecastData ? (
                <div className="d-flex align-items-center justify-content-between">
                  <div>
                    <small>{upperCase(forecastData.weather[0].description)}</small><br />
                    <small>{forecastData.main.temp}Â°C</small>
                  </div>
                  <img
                    src={weatherIcons[forecastData.weather[0].main]}
                    alt={forecastData.weather[0].description}
                    width={65}
                    style={{ filter: 'drop-shadow(0px 0px 9px #0080ff)' }}
                  />
                </div>
              ) : (
                <span>Loading...</span>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FieldControl;
