import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Ensure Bootstrap is imported

// OpenWeather API
const apiKey = '8925502a781648f4443f9e01d96c7ff5';
const apiURL = 'https://api.openweathermap.org/data/2.5/weather?units=metric&lat=';

// Helper function to format time ago
const timeAgo = (timestamp) => {
  const now = new Date();
  const diff = now - new Date(timestamp);
  const minutes = Math.floor(diff / 1000 / 60);
  const hours = Math.floor(diff / 1000 / 60 / 60);
  const days = Math.floor(diff / 1000 / 60 / 60 / 24);

  if (days > 0) return `${days} d${days > 1 ? 's' : ''} ago`;
  if (hours > 0) return `${hours} hr${hours > 1 ? 's' : ''} ago`;
  return `${minutes} min${minutes > 1 ? 's' : ''} ago`;
};

const LogsGenerator = ({ data }) => {
  const [logs, setLogs] = useState([]);
  const [weatherLogs, setWeatherLogs] = useState(new Set()); // Use a Set to hold weather logs
  const [extremeMoistureLogs, setExtremeMoistureLogs] = useState(new Set()); // Use a Set to hold extreme moisture logs
  const [extremeWeatherLogs, setExtremeWeatherLogs] = useState(new Set()); // Use a Set to hold extreme weather logs
  useEffect(() => {
    const generatedLogs = [];
    const weatherDataCache = {}; // Cache for unique weather logs
    const moistureCache = new Set(); // Cache for unique moisture logs
    const valveCache = new Set(); // Cache for unique valve status logs
    const npkCache = new Set(); // Cache for unique NPK logs

    // Process NPK values - ensure no repetition for each device
    data.device_values.farm_device_data.forEach((device) => {
      const npkLog = {
        title: `NPK Values: Nitrogen: ${device.nitrogen}, Phosphorus: ${device.phosphorus}, Potassium: ${device.potassium}`,
        timestamp: device.timestamp,
      };
      if (!npkCache.has(JSON.stringify(npkLog))) {
        generatedLogs.push(npkLog);
        npkCache.add(JSON.stringify(npkLog)); // Add to the cache
      }
    });

    // Process Valve status logs - ensure no repetition for each valve device
    data.device_values.valve_devices_data.forEach((valve) => {
      const valveLog = {
        title: `Valve Status for Section ${valve.section_name}: ${valve.valve_status} (Mode: ${valve.valve_mode})`,
        timestamp: valve.valve_timestamp,
      };
      if (!valveCache.has(JSON.stringify(valveLog))) {
        generatedLogs.push(valveLog);
        valveCache.add(JSON.stringify(valveLog)); // Add to the cache
      }
    });

    // Process extreme moisture logs - ensure no repetition for each extreme moisture condition
    const extremeMoisture = data.device_values.moisture_device_value.filter((moisture) => {
      return moisture.moisture_value <= 30 || moisture.moisture_value >= 80;
    });

    extremeMoisture.forEach((moisture) => {
      const moistureLog = {
        title: `Moisture Value for Section ${moisture.section_id}: ${moisture.moisture_value}%`,
        timestamp: moisture.timestamp,
      };
      if (!moistureCache.has(JSON.stringify(moistureLog))) {
        generatedLogs.push(moistureLog);
        moistureCache.add(JSON.stringify(moistureLog)); // Add to the cache
      }
    });

    // Find the lowest and highest moisture values
    const moistureValues = data.device_values.moisture_device_value;
    const lowestMoisture = Math.min(...moistureValues.map(moisture => moisture.moisture_value));
    const highestMoisture = Math.max(...moistureValues.map(moisture => moisture.moisture_value));

    // Create logs for the lowest and highest moisture values
    const lowestMoistureLog = moistureValues.find(moisture => moisture.moisture_value === lowestMoisture);
    const highestMoistureLog = moistureValues.find(moisture => moisture.moisture_value === highestMoisture);

    if (lowestMoistureLog) {
      generatedLogs.push({
        title: `Lowest Moisture Value for Section ${lowestMoistureLog.section_id}: ${lowestMoistureLog.moisture_value}%`,
        timestamp: lowestMoistureLog.timestamp,
      });
    }

    if (highestMoistureLog) {
      generatedLogs.push({
        title: `Highest Moisture Value for Section ${highestMoistureLog.section_id}: ${highestMoistureLog.moisture_value}%`,
        timestamp: highestMoistureLog.timestamp,
      });
    }

    // Fetch weather data and process unique weather logs
    data.farmer_farms.forEach(async (farm) => {
      const [lat, lon] = farm.farm_location.split(',').map(coord => parseFloat(coord.trim()));
      const weatherData = await fetch(`${apiURL}${lat}&lon=${lon}&appid=${apiKey}`)
        .then(response => response.json())
        .catch(err => console.error(err));

      if (weatherData) {
        const weatherLog = {
          title: `Weather Prediction for ${farm.farm_name}: ${weatherData.weather[0].description}, Temp: ${weatherData.main.temp}°C`,
          timestamp: new Date().toISOString(),
        };

        // Check if this weather log has already been added
        const cacheKey = `${farm.farm_name}_${weatherData.weather[0].description}_${weatherData.main.temp}`;
        if (!weatherDataCache[cacheKey]) {
          weatherDataCache[cacheKey] = true;
          generatedLogs.push(weatherLog);

          // Check for extreme weather conditions (Hot or Cold)
          if (weatherData.main.temp > 35) {
            const extremeHeatLog = {
              title: `Extreme Heat for ${farm.farm_name}: Temp: ${weatherData.main.temp}°C`,
              timestamp: weatherLog.timestamp,
            };
            generatedLogs.push(extremeHeatLog);
          } else if (weatherData.main.temp < 5) {
            const extremeColdLog = {
              title: `Extreme Cold for ${farm.farm_name}: Temp: ${weatherData.main.temp}°C`,
              timestamp: weatherLog.timestamp,
            };
            generatedLogs.push(extremeColdLog);
          }
        }
      }
    });

    setLogs(generatedLogs);
  }, [data]);




  return (
    <div className='borderring p-3'>
      <h5 className='text-secondary fs-6 fs-md-4 fs-lg-3'>Logs</h5>
      <table className="table table-hover">
        <thead>
          <tr>
            <th className="fs-7 fs-md-6 fs-lg-5">Title</th>
            <th className="fs-7 fs-md-6 fs-lg-5">Time Ago</th>
          </tr>
        </thead>
        <tbody>
          {/* Render Logs from NPK and Valve status */}
          {logs.map((log, index) => (
            <tr key={index}>
              <td className="fs-7 fs-md-6 fs-lg-5 ">{log.title}</td>
              <td className="fs-7 fs-md-6 fs-lg-5">{timeAgo(log.timestamp)}</td>
            </tr>
          ))}
          {/* Render Extreme Moisture Logs (Low or High Moisture) */}
          {[...extremeMoistureLogs].map((logStr, index) => {
            const log = JSON.parse(logStr); // Convert back to object
            return (
              <tr key={`extreme-moisture-${index}`}>
                <td className="fs-7 fs-md-6 fs-lg-5">{log.title}</td>
                <td className="fs-7 fs-md-6 fs-lg-5">{timeAgo(log.timestamp)}</td>
              </tr>
            );
          })}
          {/* Render Extreme Weather Logs (Hot or Cold Weather) */}
          {[...extremeWeatherLogs].map((logStr, index) => {
            const log = JSON.parse(logStr); // Convert back to object
            return (
              <tr key={`extreme-weather-${index}`}>
                <td className="fs-7 fs-md-6 fs-lg-5">{log.title}</td>
                <td className="fs-7 fs-md-6 fs-lg-5">{timeAgo(log.timestamp)}</td>
              </tr>
            );
          })}
          {/* Render Weather Logs */}
          {[...weatherLogs].map((logStr, index) => {
            const log = JSON.parse(logStr); // Convert back to object
            return (
              <tr key={`weather-${index}`}>
                <td className="fs-7 fs-md-6 fs-lg-5">{log.title}</td>
                <td className="fs-7 fs-md-6 fs-lg-5">{timeAgo(log.timestamp)}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );

};

export default LogsGenerator;
