import React, { useState } from 'react';

const FieldControl = () => {
    const [inputDuration, setInputDuration] = useState(''), [isManualMode, setIsManualMode] = useState(false), [isStarted, setIsStarted] = useState(false);
    const handleManualClick = () => { setIsManualMode(true); setIsStarted(false); setInputDuration(''); };
    const handleStartClick = () => { if (inputDuration) setIsStarted(true); };
    const handleInputChange = (e) => { setInputDuration(e.target.value); if (!isStarted) setIsStarted(false); };
    const handleSaveChanges = (e) => { e.preventDefault(); alert(`Duration saved: ${inputDuration}`); };

    return (
        <div>
            <h6 className='text-secondary'>Farm controls</h6>
            <div className='controller px-3 py-2'>
                <div className="d-flex justify-content-evenly text-center">
                    <button type="button" className="btn btn-secondary w-100 btn-sm">Auto</button>
                    <span className="text-secondary fs-6 d-flex justify-content-center align-items-center px-3">Recommended</span>
                </div>
                <button type="button" className="btn btn-secondary btn-sm mt-2 w-100" onClick={handleManualClick}>Manual</button>
                {isManualMode && (
                    <form className='mt-2 border p-2' onSubmit={handleSaveChanges}>
                        <input className="form-control form-control-sm mt-1 mb-2" type="number" placeholder="Enter duration" value={inputDuration} onChange={handleInputChange} />
                        <div className='d-flex justify-content-evenly align-items-center'>
                            <button type="button" className="btn btn-outline-secondary btn-sm w-100 me-3" onClick={handleStartClick} disabled={!inputDuration}>Start</button>
                            <button type="button" className="btn btn-outline-secondary btn-sm w-100">Stop</button>
                        </div>
                        <div className='text-center my-2'>
                            <button type="submit" className='btn btn-outline-secondary btn-sm' disabled={!isStarted}>Save Changes</button>
                        </div>
                    </form>
                )}
            </div>
        </div>
    );
}

export default FieldControl;
