import React, { useState, useEffect } from 'react';
import { OverlayTrigger, Popover } from 'react-bootstrap';
import clouds from './WeatherImages/clouds.png';
import clear from './WeatherImages/clear.png';
import drizzle from './WeatherImages/drizzle.png';
import rain from './WeatherImages/rain.png';
import mist from './WeatherImages/mist.png';

const apikey = "8925502a781648f4443f9e01d96c7ff5";
const apiURL = "https://api.openweathermap.org/data/2.5/forecast?units=metric&lat=";

const FieldControl = ({ activeDevices }) => {
    const { total_device, total_active_devices, auto_threshold_value, prediction_location } = activeDevices;
    const [inputDuration, setInputDuration] = useState('');
    const [isManualMode, setIsManualMode] = useState(false);
    const [isStarted, setIsStarted] = useState(false);
    const [threshold1, setThreshold1] = useState(auto_threshold_value?.auto_on_threshold || 30);
    const [threshold2, setThreshold2] = useState(auto_threshold_value?.auto_off_threshold || 30);
    const [forecastData, setForecastData] = useState(null);
    const [isEditingThreshold1, setIsEditingThreshold1] = useState(false);
    const [isEditingThreshold2, setIsEditingThreshold2] = useState(false);

    const [latitude, longitude] = prediction_location.split(", ").map(Number);

    useEffect(() => {
        if (latitude && longitude) {
            fetch(`${apiURL}${latitude}&lon=${longitude}&appid=${apikey}`)
                .then(response => response.json())
                .then(data => {
                    const forecast = data.list[2]; // 6-hour forecast
                    setForecastData(forecast);
                })
                .catch(error => console.error("Error fetching weather forecast data:", error));
        }
    }, [latitude, longitude]);

    const handleThresholdChange1 = (e) => setThreshold1(e.target.value);
    const handleThresholdChange2 = (e) => setThreshold2(e.target.value);
    const handleInputChange = (e) => setInputDuration(e.target.value);
    const handleStartClick = () => inputDuration && setIsStarted(true);

    const manualPopover = (
        <Popover id="popover-manual">
            <Popover.Body>
                <form>
                    <input
                        className="form-control form-control-sm mt-1 mb-2"
                        type="number"
                        placeholder="Enter duration"
                        value={inputDuration}
                        onChange={handleInputChange}
                    />
                    <div className="d-flex justify-content-evenly align-items-center">
                        <button
                            type="button"
                            className="btn btn-outline-secondary btn-sm w-100 me-3"
                            onClick={handleStartClick}
                            disabled={!inputDuration}
                        >
                            Start
                        </button>
                        <button
                            type="button"
                            className="btn btn-outline-secondary btn-sm w-100"
                        >
                            Stop
                        </button>
                    </div>
                    <div className="text-center my-2">
                        <button
                            type="submit"
                            className="btn btn-outline-secondary btn-sm"
                            disabled={!isStarted}
                        >
                            Save Changes
                        </button>
                    </div>
                </form>
            </Popover.Body>
        </Popover>
    );

    const weatherIcons = {
        Clouds: clouds,
        Clear: clear,
        Drizzle: drizzle,
        Rain: rain,
        Mist: mist,
    };

    return (
        <div>
            <div className="controller">
                <div className="container">
                    <div className="row pt-2 pb-1">
                        <div className="col-12 col-sm-3">
                            <h6 className='text-secondary'>Farm devices</h6>
                            <small>Total Devices : <span className='fw-bold fs-6'>{total_device}</span></small><br />
                            <small>Active Devices : <span className='fw-bold fs-6'>{total_active_devices}</span></small>
                        </div>
                        <div className="col-12 col-sm-3">
                            <h6 className="text-secondary m-0">Threshold</h6>
                            {/* Threshold 1 */}
                            <div className="d-flex align-items-center">
                                <small>Auto on threshold :</small>
                                <input type="number" value={threshold1} onChange={handleThresholdChange1} className="form-control form-control-sm ms-2" />
                            </div>
                            {/* Threshold 2 */}
                            <div className="d-flex align-items-center">
                                <small>Auto off threshold :</small>
                                <input type="number" value={threshold2} onChange={handleThresholdChange2} className="form-control form-control-sm ms-2" />
                            </div>
                        </div>
                        {/* Weather */}
                        <div className='col-12 col-sm-3'>
                            <h6 className='text-secondary m-0'>Weather in 6 hrs</h6>
                            {forecastData ? (
                                <div className='d-flex align-items-center'>
                                    <small>{forecastData.main.temp}Â°C</small>
                                    <img src={weatherIcons[forecastData.weather[0].main]} alt={forecastData.weather[0].description} width={65} />
                                </div>
                            ) : (
                                <span>Loading...</span>
                            )}
                        </div>
                        {/* Pump Controls */}
                        <div className="col-12 col-sm-3">
                            <h6 className='text-secondary'>Pump controls</h6>
                            <OverlayTrigger trigger="click" placement="bottom" overlay={manualPopover}>
                                <button type="button" className="btn btn-outline-secondary btn-sm">Manual</button>
                            </OverlayTrigger>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default FieldControl;
