import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Spinner from "react-bootstrap/Spinner";
import logo from "./Images/logo.png";
import "./CSS/User.css";
import servererror from './Images/server.jpg'
import "bootstrap/dist/css/bootstrap.min.css";
import NavBar from "./Components/NavBar";
import Farmer from "./Components/Farmer/Farmer";
import FarmSensorsControl from "./Components/Farmer/FarmSensorsControl";
import Farms from "./Components/Farmer/Farms";
import MapContainer from "./Components/Farmer/Map";
import Sections from "./Components/Farmer/Sections";
import Logs from "./Components/Farmer/Logs";
import Modal from "./Components/Farmer/Modal";
import FieldControl from "./Components/Farmer/FieldControl";

const User = () => {
  const { id } = useParams();
  const [rec, setRec] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [chartType, setChartType] = useState('timeline');

  useEffect(() => {
    setLoading(true);
    setError(null);

    fetch(`/farmer/${id}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setRec(data);
        setLoading(false);



      })
      .catch((error) => {
        console.error("Error fetching user data:", error);
        setError(error.message);
        setLoading(false);
      });
  }, [id]);

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: "100vh" }}>
        <Spinner
          animation="border"
          variant="secondary"
          role="status"
          style={{ width: "5rem", height: "5rem" }} 
        >
          <span className="sr-only">Loading...</span>
        </Spinner>
      </div>
    );
  }


  if (error) {
    return (
      <div className="d-flex flex-column align-items-center justify-content-center vh-100 vw-100 text-center">
          <a href="/" className="d-flex align-items-center text-decoration-none">
          <img
            src={logo}
            alt="logo"
            className="me-3"
            style={{ width: "25px" }}
          />
          <span className="fs-4 fw-semibold text-dark d-none d-sm-block">
            Smart Irrigation & Fartigation
          </span>
        </a>
        <br></br>
        <p className="fs-5 text-secondary" style={{fontFamily:'unset'}} >{error}</p>
        <img src={servererror} width={250} alt="Server Error" /> <br></br>
      </div>
    );
  }



  function countRecentMoistureValues(moistureDeviceValues) {
    const currentTimestamp = new Date();
    const oneHourInMillis = 60 * 60 * 1000; // 1 hour in milliseconds

    const recentValues = moistureDeviceValues.filter(item => {
      const itemTimestamp = new Date(item.timestamp);
      const timeDifference = currentTimestamp - itemTimestamp;

      return timeDifference <= oneHourInMillis;
    });

    return recentValues.length;
  }

  const collected_data = rec;


  const famer_details = rec?.farmer_details;
  const farm_details = rec?.farmer_farms;

  const locationCoordinates = rec?.location_coordinates;
  const device_values = rec?.device_values



  const prediction_location = rec?.location_coordinates.farm_device[0].device_location;

  const farm_devices_data = rec?.device_values.farm_device_data;

  const section_data = rec?.device_values.valve_devices_data;

  const total_devices = rec?.location_coordinates.section_device.length + 1;
  const active_counting = rec?.device_values.moisture_device_value;


  const total_active_count = countRecentMoistureValues(active_counting);
  const auto_threshold_value = rec?.farm_details;

  const farm_name = rec?.farm_details.farm_name;  

  const total_and_active_farm_devices = {
    total_device: total_devices,
    total_active_devices: total_active_count,
    auto_threshold_value: auto_threshold_value,
    prediction_location: prediction_location
  }
const getTimeAgo = (timestamp) => {
  const now = new Date();
  const diffInSeconds = Math.floor((now - new Date(timestamp)) / 1000);
  const diffInMinutes = Math.floor(diffInSeconds / 60);
  const diffInHours = Math.floor(diffInMinutes / 60);
  const diffInDays = Math.floor(diffInHours / 24);

  if (diffInSeconds < 60) return `${diffInSeconds} sec${diffInSeconds === 1 ? '' : 's'} ago`;
  if (diffInMinutes < 60) return `${diffInMinutes} min${diffInMinutes === 1 ? '' : 's'} ago`;
  if (diffInHours < 24) return `${diffInHours} hr${diffInHours === 1 ? '' : 's'} ago`;
  return `${diffInDays} d${diffInDays === 1 ? '' : 's'} ago`;
};

// Function to get weather forecast for extreme conditions
const getWeatherForecast = async (latitude, longitude) => {
  const apikey = "8925502a781648f4443f9e01d96c7ff5";
  const apiURL = `https://api.openweathermap.org/data/2.5/onecall?lat=${latitude}&lon=${longitude}&units=metric&exclude=current,minutely,hourly&appid=${apikey}`;

  try {
    const response = await fetch(apiURL);
    const data = await response.json();

    // Check for extreme weather conditions
    const extremeConditions = [];
    data.daily.forEach((day) => {
      if (day.weather.some((condition) => condition.main === "Rain")) {
        extremeConditions.push("Rain is expected");
      }
      if (day.temp.min < 10) {
        extremeConditions.push("Cold temperatures forecasted");
      }
      if (day.temp.max > 35) {
        extremeConditions.push("Extreme heat forecasted");
      }
      if (day.temp.min > 30 && day.rain === undefined) {
        extremeConditions.push("Possible drought conditions");
      }
    });

    return extremeConditions;
  } catch (error) {
    console.error("Error fetching weather data:", error);
    return [];
  }
};

const getNotifications = async (data) => {
  try {
    const notifications = [];

    // Helper function for adding notifications
    const addNotification = (message, timestamp) => {
      notifications.push({
        message,
        time: getTimeAgo(timestamp),
      });
    };

    // 1. Low and high moisture levels
    data.device_values.moisture_device_value.forEach((moisture) => {
      if (moisture.moisture_value < data.farm_details.auto_on_threshold) {
        addNotification(`Low moisture in section ${moisture.section_id}`, moisture.timestamp);
      }
      if (moisture.moisture_value > data.farm_details.auto_off_threshold) {
        addNotification(`High moisture in section ${moisture.section_id}`, moisture.timestamp);
      }
    });

    // 2. Valve status updates (with mode)
    data.device_values.valve_devices_data.forEach((valve) => {
      addNotification(
        `Valve in section ${valve.section_name} is now ${valve.valve_status} (Mode: ${valve.valve_mode === "auto" ? "Automatic" : "Manual"})`,
        valve.valve_timestamp
      );
    });

    // 3. Fertilizer reminder
    data.device_values.farm_device_data.forEach((device) => {
      if (device.nitrogen < 50) addNotification("Low nitrogen, consider fertilizing", device.timestamp);
      if (device.phosphorus < 50) addNotification("Low phosphorus, consider fertilizing", device.timestamp);
      if (device.potassium < 50) addNotification("Low potassium, consider fertilizing", device.timestamp);
    });

    // 4. Temperature out of range
    data.device_values.farm_device_data.forEach((device) => {
      if (device.temperature < 15 || device.temperature > 35) {
        addNotification(`Temperature out of range for farm device ${device.farm_device_id}`, device.timestamp);
      }
    });

    // 5. High humidity levels
    data.device_values.farm_device_data.forEach((device) => {
      if (device.humidity > 80) {
        addNotification(`High humidity detected in farm device ${device.farm_device_id}`, device.timestamp);
      }
    });

    // 6. Valve mode change (auto to manual or manual to auto)
    data.device_values.valve_devices_data.forEach((valve) => {
      if (valve.valve_mode !== "auto") {
        addNotification(`Valve mode in section ${valve.section_name} changed to ${valve.valve_mode === "auto" ? "Automatic" : "Manual"}`, valve.valve_timestamp);
      }
    });

    // 7. Device connection issues
    data.device_values.farm_device_data.forEach((device) => {
      if (device.status === "offline") {
        addNotification(`Device ${device.device_name} is offline`, device.timestamp);
      }
    });

    // 8. Upcoming scheduled fertilization
    data.device_values.farm_device_data.forEach((device) => {
      if (device.nitrogen === 70 && device.phosphorus === 80 && device.potassium === 90) {
        addNotification("Upcoming fertilization scheduled for farm device", device.timestamp);
      }
    });

    // 9. Low moisture across multiple sections
    const lowMoistureSections = data.device_values.moisture_device_value.filter(
      (moisture) => moisture.moisture_value < data.farm_details.auto_on_threshold
    );
    if (lowMoistureSections.length > 2) {
      addNotification("Multiple sections with low moisture, consider irrigation", new Date());
    }

    // 10. Get weather conditions for extreme weather notifications
    const extremeConditions = await getWeatherForecast(data.farm_details.latitude, data.farm_details.longitude);
    if (extremeConditions.length > 0) {
      extremeConditions.forEach((condition) => {
        addNotification(condition, new Date());
      });
    }

    return notifications;

  } catch (error) {
    console.error("Error generating notifications:", error);
    return [];
  }
};



