import React, { useState } from 'react';
import ProgressChart from './ProgressChart';

const FarmSensorsControl = ({ collected_data = {}, farmer_details = {} }) => {
  const farm_devices_data = collected_data?.device_values?.farm_device_data || [];
  const farm_id = collected_data?.farm_details?.farm_id;
  const farmname = collected_data?.farm_details?.farm_name || "Farm Name Not Available";
  const farmer_id = farmer_details?.farmer_details?.farmer_id;

  const units = {
    nitrogen: 'kg/ha',
    phosphorus: 'kg/ha',
    potassium: 'kg/ha',
    temperature: '°C',
    humidity: '%',
    avg_moisture: '%',
  };

  const capitalize = (name) => name.charAt(0).toUpperCase() + name.slice(1).toLowerCase();

  // Handle timestamp
  const lastUpdated = farm_devices_data.length > 0 
    ? new Date(farm_devices_data[0]?.timestamp).toLocaleString() 
    : 'N/A';

  // States for farm name editing
  const [isEditing, setIsEditing] = useState(false);
  const [newFarmName, setNewFarmName] = useState(farmname);

  const handleFarmNameChange = (event) => {
    setNewFarmName(event.target.value);
  };

  const saveFarmName = async () => {
    if (!farm_id || !farmer_id) {
      console.error("Invalid farm or farmer details.");
      return;
    }

    try {
      const response = await fetch(`/farmer/farm/farm_name/${farm_id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ farm_name: newFarmName, farmer_id }),
      });

      if (response.ok) {
        const result = await response.json();
        console.log('Farm name updated successfully:', result);
        setIsEditing(false);
      } else {
        const errorText = await response.text();
        console.error('Failed to update farm name:', errorText);
      }
    } catch (error) {
      console.error('Error updating farm name:', error);
    }
  };

  return (
    <div>
      {/* Farm Header */}
      <div className='text-secondary d-flex justify-content-between align-items-center'>
        <div>
          {isEditing ? (
            <div>
              <input
                type="text"
                value={newFarmName}
                onChange={handleFarmNameChange}
                className="form-control"
                style={{ width: '200px', display: 'inline', height: '35px' }}
              />
              <button
                onClick={saveFarmName}
                className="btn btn-outline-secondary btn-sm ms-2"
              >
                Save
              </button>
            </div>
          ) : (
            <div>
              <span className='text-dark fs-5 fw-bold'>{capitalize(newFarmName)}</span>
              <i
                className="fa-solid fa-pencil ms-2"
                style={{ cursor: 'pointer' }}
                onClick={() => setIsEditing(true)}
              />
            </div>
          )}
        </div>
        <div className="d-flex align-items-center" aria-live="polite">
          <span className="fw-bold text-dark">Active</span>
          <div
            className="spinner-grow text-success ms-2"
            role="status"
            style={{ width: "0.75rem", height: "0.75rem" }}
          >
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </div>

      {/* Last Updated */}
      <small className='text-secondary'>Last updated: {lastUpdated}</small>

      {/* Progress Charts */}
      <div
        className="ProgressCharts"
        style={{
          width: "100%",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <div
          className="Sensors"
          style={{
            width: "100%",
            display: "grid",
            gap: "10px",
            padding: "10px",
          }}
        >
          <style>
            {`
              .Sensors {
                grid-template-columns: repeat(1, 1fr);
              }
              @media (min-width: 768px) {
                .Sensors {
                  grid-template-columns: repeat(3, 1fr);
                }
              }
              @media (min-width: 1024px) {
                .Sensors {
                  grid-template-columns: repeat(auto-fit, minmax(100px, 1fr));
                }
              }
              @media (max-width: 767px) {
                .Sensors {
                  grid-template-columns: repeat(2, 1fr);
                }
              }
            `}
          </style>
          {farm_devices_data[0] &&
            Object.entries(farm_devices_data[0]).map(([name, value], index) =>
              name !== "timestamp" && name !== "farm_device_id" ? (
                <div
                  className="col"
                  key={index}
                  style={{
                    textAlign: "center",
                    boxSizing: "border-box",
                  }}
                >
                  <ProgressChart
                    value={Math.round(value)}
                    name={capitalize(name)}
                    unit={units[name] || 'mg/kg'}
                  />
                </div>
              ) : null
            )}
        </div>
      </div>
    </div>
  );
};

export default FarmSensorsControl;
