import React, { useEffect } from 'react';
import servererror from './User/Images/'; // Ensure the correct path to the error image

function ErrorComponent({ error }) {
  useEffect(() => {
    // SVG Drawing Logic
    const svg = document.getElementById('error-svg');
    const g = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    svg.appendChild(g);
    const colours = [];
    const size = 15;
    const direction = ['up', 'down', 'left', 'right'];

    function setSvgSize() {
      svg.setAttribute('width', window.innerWidth);
      svg.setAttribute('height', window.innerHeight);
    }

    function initColours() {
      for (let i = 0; i < 10; i++) {
        let randomColor = `#${Math.floor(Math.random() * 16777215).toString(16)}`;
        colours.push({ item: i, colour: randomColor });
      }
    }

    function draw() {
      const svgB = svg.getBoundingClientRect();
      const x = svgB.width / 2;
      const y = svgB.height / 2;

      for (let i = 0; i < 10; i++) {
        const path = document.createElementNS('http://www.w3.org/2000/svg', 'path');
        const colour = colours[i].colour;
        path.setAttribute('stroke', colour);
        path.setAttribute('stroke-width', '2');
        let d = `M ${x} ${y} `;
        const randomDirection = direction[Math.floor(Math.random() * direction.length)];

        if (randomDirection === 'up') d += `L ${x} ${y - size}`;
        else if (randomDirection === 'down') d += `L ${x} ${y + size}`;
        else if (randomDirection === 'left') d += `L ${x - size} ${y}`;
        else if (randomDirection === 'right') d += `L ${x + size} ${y}`;

        path.setAttribute('d', d);
        g.appendChild(path);
      }
    }

    window.addEventListener('resize', () => {
      setSvgSize();
      g.innerHTML = ''; // Clear previous paths on resize
      draw();
    });

    setSvgSize();
    initColours();
    draw();
    const interval = setInterval(draw, 100);

    return () => {
      clearInterval(interval);
      svg.innerHTML = ''; // Clean up SVG content on unmount
      window.removeEventListener('resize', setSvgSize);
    };
  }, []);

  return (
    <div className="d-flex flex-column align-items-center justify-content-center vh-100 vw-100 text-center" style={{ position: 'relative' }}>
      <svg id="error-svg" style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', background: 'black' }}></svg>
      <img src={servererror} width={250} alt="Server Error" style={{ position: 'relative', zIndex: 1 }} />
      <p className="fs-4 fw-bold text-secondary" style={{ fontFamily: 'unset', position: 'relative', zIndex: 1 }}>
        {error}
      </p>
    </div>
  );
}

export default ErrorComponent;
