import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

const AdminFarm = () => {
    const location = useLocation();
    const { farmerId, admin_id } = location.state || {};
    const [farmerDetails, setFarmerDetails] = useState(null);
    const [farmDetails, setFarmDetails] = useState([]); // State for farm details
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if (farmerId && admin_id) {
            const fetchDetails = async () => {
                try {
                    const response = await fetch('/admin/farmer', {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json'
                        },
                        body: JSON.stringify({
                            admin_id: admin_id,
                            farmer_id: farmerId
                        })
                    });
                    const data = await response.json();
                    setFarmerDetails(data.farmer_data); // Assuming the response has farmer_data
                    setFarmDetails(data.farm_data); // Assuming the response has farm_data
                    setLoading(false);
                } catch (error) {
                    console.error('Error fetching details:', error);
                    setLoading(false);
                }
            };
            fetchDetails();
        }
    }, [farmerId, admin_id]);

    if (loading) {
        return (
            <div className="d-flex justify-content-center align-items-center" style={{ height: '100vh' }}>
                <div className="spinner-border" role="status">
                    <span className="visually-hidden">Loading...</span>
                </div>
            </div>
        );
    }

    if (!farmerDetails) {
        return <div>No farmer details found.</div>;
    }

    return (
        <div className='container my-5'>
            <div className=''>
                <div className="container">
                    <div className="row">
                        <div className="col-4">
                            <div className='farmer_details borderring'>
                                <h5 className='text-secondary'>Farmer Details</h5>
                                <p>ID: {farmerDetails.farmer_id}</p><i class="fa-solid fa-pencil"></i>
                                <p>Name: {farmerDetails.farmer_fname} {farmerDetails.farmer_lname}</p>
                                <p>Phone: {farmerDetails.farmer_phone}</p>
                                <p>Email: {farmerDetails.farmer_email}</p>
                                <p>Join Date: {new Date(farmerDetails.join_date).toLocaleDateString()}</p>
                                <p>Total Farms: {farmerDetails.total_farms}</p>
                            </div>
                        </div>
                        <div className="col">
                            <div className='borderring'  >
                                <div className='d-flex aligh-items-center justify-content-between'>
                                    <h5 className='text-secondary'>Farms</h5>
                                    <div>
                                        <button className='btn btn-sm btn-secondary me-2'>Add Farm</button>
                                        <button className='btn btn-sm btn-secondary'>Edit Farm</button>
                                    </div>
                                </div>
                                <div className='farms-farmer' style={{ maxHeight: '35vh', overflow: 'auto' }} >
                                    <table className="table table-hover">
                                        <thead className="sticky-header">
                                            <tr>
                                                <th>Farm Name</th>
                                                <th>Farm Size (acres)</th>
                                                <th>Creation Date</th>
                                                <th>Auto On Threshold</th>
                                                <th>Auto Off Threshold</th>
                                                <th>Location Coordinates</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {farmDetails.map((farm, index) => (
                                                <tr key={index}>
                                                    <td>{farm.farm_name}</td>
                                                    <td>{farm.farm_size}</td>
                                                    <td>{new Date(farm.creation_date).toLocaleDateString()}</td>
                                                    <td>{farm.auto_on_threshold}</td>
                                                    <td>{farm.auto_off_threshold}</td>
                                                    <td>
                                                        <ul>
                                                            {farm.farm_location_cordinates.map((coord, idx) => (
                                                                <li key={idx}>{coord}</li>
                                                            ))}
                                                        </ul>
                                                    </td>
                                                </tr>
                                            ))}
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className='w-100 borderring mt-3'>
                        {/* Additional content can go here */}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AdminFarm;
