import React, { useEffect, useState } from 'react';
import './User/CSS/User.css'; // Include the CSS from your original code
import 'boxicons/css/boxicons.min.css';
import { json } from 'react-router-dom';

const LoginSignup = () => {
  const [isActive, setIsActive] = useState(false);
  const [isAuthorised, setAuthorized] = useState(false);
  const [isuser ,setUser]=useEffect([]);


  async function authorizeduser(userID) {
    const user = await fetch('/login/${userID}',{
      method:'GET',
      headers : {"Content-Type" : "application/json"},
      body: JSON.stringify({
        "user": 1001,
        "password":"Farmer",
        "type":"user"
    })
    });
    const data = await JSON(user);
    console.log(data);
  }

  authorizeduser()

  return (
    <div className='bodyLogin'>
      <div className={`containeradmin ${isActive ? 'active' : ''}`}>
        {/* Login Form */}
        <div className="form-box login">
          <form action="#">
            <h1>Login as User</h1>
            <div className="input-box">
              <input type="text" placeholder="Username" required />
              <i className="bx bxs-user"></i>
            </div>
            <div className="input-box">
              <input type="password" placeholder="Password" required />
              <i className="bx bxs-lock-alt"></i>
            </div>
            <div className="forgot-link">
              <a href="#">Forgot Password?</a>
            </div>
            <button type="submit" className="BTN">
              Login
            </button>
          </form>
        </div>

        {/* Registration Form */}
        <div className="form-box register">
          <form action="#">
            <h1>Login as Admin</h1>
            <div className="input-box">
              <input type="text" placeholder="Email or ID" required />
              <i className="bx bxs-envelope"></i>
            </div>
            <div className="input-box">
              <input type="password" placeholder="Password" required />
              <i className="bx bxs-lock-alt"></i>
            </div>
            <button type="submit" className="BTN">
              Login
            </button>
          </form>
        </div>

        {/* Toggle Panels */}
        <div className="toggle-box">
          <div className="toggle-panel toggle-left">
            <h1>Smart Agriculture</h1>

            <p></p>
            <button
              className="BTN register-BTN"
              onClick={() => setIsActive(true)}
            >
              Admin
            </button>
          </div>
          <div className="toggle-panel toggle-right">
            <h1>Smart Agriculture</h1>
            <p></p>
            <button
              className="BTN login-BTN"
              onClick={() => setIsActive(false)}
            >
              User
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginSignup;
