import React, { useState, useEffect } from "react";
import { OverlayTrigger, Popover } from "react-bootstrap";
import clouds from "./WeatherImages/clouds.png";
import clear from "./WeatherImages/clear.png";
import drizzle from "./WeatherImages/drizzle.png";
import rain from "./WeatherImages/rain.png";
import mist from "./WeatherImages/mist.png";
const apikey = "8925502a781648f4443f9e01d96c7ff5";
const apiURL =
  "https://api.openweathermap.org/data/2.5/forecast?units=metric&lat=";
const FieldControl = ({ collected_data, farmer_details = [] }) => {
  const [inputDuration, setInputDuration] = useState(30); // Default value set to 30 const [isManualMode, setIsManualMode] = useState(false); const [isStarted, setIsStarted] = useState(false); const [threshold1, setThreshold1] = useState(30); const [threshold2, setThreshold2] = useState(30); const [forecastData, setForecastData] = useState(null); const [isEditingThreshold1, setIsEditingThreshold1] = useState(false); const [isEditingThreshold2, setIsEditingThreshold2] = useState(false); const [recomended, setRecomendation] = useState(false); const activeDevices = { total_device: collected_data?.location_coordinates.section_device.length || 0, total_active_devices: 0, // Placeholder, you can add logic to calculate this auto_threshold_value: collected_data?.farm_details, prediction_location: collected_data?.location_coordinates?.farm_device?.[0]?.device_location || '' }; const [latitude, longitude] = activeDevices.prediction_location ? activeDevices.prediction_location.split(", ").map(Number) : [null, null]; useEffect(() => { if (activeDevices.auto_threshold_value) { const { auto_on_threshold, auto_off_threshold } = activeDevices.auto_threshold_value; setThreshold1(auto_on_threshold || 30); setThreshold2(auto_off_threshold || 30); } }, [activeDevices]); useEffect(() => { if (latitude && longitude) { fetch(`${apiURL}${latitude}&lon=${longitude}&appid=${apikey}`) .then(response => response.json()) .then(data => { const forecast = data.list[2]; setForecastData(forecast); }) .catch(error => console.error("Error fetching weather forecast data:", error)); } }, [latitude, longitude]); const handleThresholdChange1 = (e) => setThreshold1(e.target.value); const handleThresholdChange2 = (e) => setThreshold2(e.target.value); const toggleEditThreshold1 = () => setIsEditingThreshold1(!isEditingThreshold1); const toggleEditThreshold2 = () => setIsEditingThreshold2(!isEditingThreshold2); const handleManualClick = () => { setIsManualMode(true); setIsStarted(false); setInputDuration(30); // Resetting to default value when switching to manual mode }; const handleStartClick = () => { if (inputDuration) setIsStarted(true); }; const handleInputChange = (e) => setInputDuration(e.target.value); const handleSaveChanges = async (e) => { e.preventDefault(); if (!inputDuration || !isStarted) return; try { const response = await fetch(`/farm/valve/${farmer_details[0]?.valve_id}`, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ mode: 'manual', status: 'on', timer: inputDuration, farmer_id: farmer_details[0]?.farmer_id // Assuming you have farmer_id in farmer_details }) }); const result = await response.json(); if (response.ok) { alert(`Duration saved: ${inputDuration} minutes. Response: ${result.message}`); } else { alert(`Error: ${result.error}`); } } catch (error) { console.error("Error saving duration:", error); } }; const weatherIcons = { Clouds: clouds, Clear: clear, Drizzle: drizzle, Rain: rain, Mist: mist, }; const upperCase = (name) => name.charAt(0).toUpperCase() + name.slice(1).toLowerCase(); const manualPopover = ( <Popover id="popover-manual"> <Popover.Body> <form onSubmit={handleSaveChanges}> <input className="form-control form-control-sm mt-1 mb-2" type="number" placeholder="Enter duration" value={inputDuration} onChange={handleInputChange} min={1} // Added to ensure a positive number /> <div className="d-flex justify-content-evenly align-items-center"> <button type="button" className="btn btn-outline-secondary btn-sm w-100 me-3" onClick={handleStartClick} disabled={!inputDuration} > Start </button> <button type="button" className="btn btn-outline-secondary btn-sm w-100" > Stop </button> </div> <div className="text-center my-2"> <button type="submit" className="text-center btn btn-outline-secondary btn-sm" disabled={!isStarted} > Save

  return (
    <div>
      <hr className="my-1"></hr>
      <div className="controller">
        <div className="container">
          <div className="row pt-2 pb-1">
            <div className="col-12 col-sm-3 field-control-mobile">
              <h6 className="text-secondary">Farm devices</h6>
              <small>
                Total Devices :{" "}
                <span className="fw-bold fs-6">
                  {activeDevices.total_device > 0
                    ? activeDevices.total_device + 1
                    : 0}
                </span>
              </small>
              <br />
              <small>
                Active Devices :{" "}
                <span className="fw-bold fs-6">
                  {activeDevices.total_active_devices}
                </span>
              </small>
            </div>
            <div className="col-12 col-sm-3 field-control-mobile">
              <div className="d-flex">
                <h6 className="text-secondary me-2 m-0">
                  Auto threshold values
                </h6>
                <i
                  className="fa-solid fa-pen text-secondary"
                  style={{ cursor: "pointer" }}
                ></i>
              </div>

              <div className="d-flex align-items-center mt-2">
                <small>On threshold : </small>
                {isEditingThreshold1 ? (
                  <>
                    <input
                      type="number"
                      value={threshold1}
                      onChange={handleThresholdChange1}
                      className="form-control form-control-sm ms-2"
                      style={{ width: "60px" }}
                      min={10}
                      max={30}
                    />
                    <button
                      onClick={toggleEditThreshold1}
                      className="btn btn-sm btn-outline-secondary ms-2"
                    >
                      Save
                    </button>
                  </>
                ) : (
                  <>
                    <small>{threshold1}</small>
                    <button
                      onClick={toggleEditThreshold1}
                      className="btn btn-sm ms-1 text-secondary "
                    ></button>
                  </>
                )}
              </div>
              <div className="d-flex align-items-center mt-1">
                <small>Off threshold : </small>
                {isEditingThreshold2 ? (
                  <>
                    <input
                      type="number"
                      value={threshold2}
                      onChange={handleThresholdChange2}
                      className="form-control form-control-sm ms-2"
                      style={{ width: "60px" }}
                    />
                    <button
                      onClick={toggleEditThreshold2}
                      className="btn btn-sm btn-outline-secondary ms-1"
                    >
                      Save
                    </button>
                  </>
                ) : (
                  <>
                    <small>{threshold2}</small>
                    <button
                      onClick={toggleEditThreshold2}
                      className="btn btn-sm ms-2 text-secondary"
                    ></button>
                  </>
                )}
              </div>
              {/* <div>
                <form className='borderring'>
                  <input type='number' placeholder='Enter auto on threshold value' style={{width:'100%'}}/>
                  <input type='number' placeholder='Enter auto off threshold value' className='my-1' style={{width:'100%'}} />
                  <button >Save</button>
                </form>
              </div> */}
            </div>
            <div className="col-12 col-sm-3 field-control-mobile">
              <h6 className="text-secondary m-0">Weather in 6 hrs</h6>
              {forecastData ? (
                <div className="d-flex align-items-center justify-content-between">
                  <div>
                    <small>
                      {upperCase(forecastData.weather[0].description)}
                    </small>
                    <br></br>
                    <small>{forecastData.main.temp}Â°C</small>
                  </div>
                  <img
                    src={weatherIcons[forecastData.weather[0].main]}
                    alt={forecastData.weather[0].description}
                    width={65}
                    style={{ filter: "drop-shadow(0px 0px 9px #0080ff)" }}
                  />
                </div>
              ) : (
                <span>Loading...</span>
              )}
            </div>
            <div className="col-12 col-sm-3 toppadding">
              <h6 className="text-secondary">Pump controls</h6>
              <div className="d-flex justify-content-evenly text-center flex-column">
                <div className="d-flex">
                  <button
                    type="button"
                    className="btn btn-outline-secondary btn-sm w-100 mb-1"
                  >
                    Auto
                  </button>
                  {recomended && (
                    <span className="text-secondary ms-2">Recommended</span>
                  )}
                </div>
                <OverlayTrigger
                  trigger="click"
                  placement="bottom"
                  overlay={manualPopover}
                >
                  <button
                    type="button"
                    className="btn btn-outline-secondary btn-sm w-100"
                    onClick={handleManualClick}
                  >
                    Manual
                  </button>
                </OverlayTrigger>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FieldControl;
