import React, { useEffect, useRef, useState } from "react";
import L from "leaflet";
import 'leaflet/dist/leaflet.css';

const Map = ({ jsonData }) => {
    const scrollerRef = useRef(null);
    const mapRef = useRef(null);
    const [legendItems, setLegendItems] = useState([]);

    useEffect(() => {
        // Handle horizontal scrolling with the mouse wheel
        const handleWheel = (event) => {
            event.preventDefault();
            if (scrollerRef.current) {
                scrollerRef.current.scrollLeft += event.deltaY;
            }
        };

        const scroller = scrollerRef.current;
        if (scroller) {
            scroller.addEventListener("wheel", handleWheel, { passive: false });
        }

        return () => {
            if (scroller) {
                scroller.removeEventListener("wheel", handleWheel);
            }
        };
    }, []);

    useEffect(() => {
        if (mapRef.current && jsonData) {
            // Create the map centered at the given latitude and longitude
            const map = L.map(mapRef.current).setView([16.22525, 74.8424], 16); // Adjusted zoom level to 16 for better visibility

            // Add OpenStreetMap tile layer
            L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
                maxZoom: 29,
                minZoom: 2,
            }).addTo(map);

            // Create a set for unique sensor types (for the legend)
            const uniqueTypes = new Set();

            // Define color map for different sensor types
            const colorMap = {
                farmland: "green",
                valve: "orange",
                moisture: "blue",
                npk: "red",
            };

            let farmlandBounds;

            // Process the map data from jsonData.location_coordinates
            // Add Farmland coordinates as a polygon
            const farmlandCoordinates = jsonData.location_coordinates.farm_coordinates.map((coord) => {
                const [lat, lon] = coord.split(',');
                return [parseFloat(lat), parseFloat(lon)];
            });

            const farmlandLayer = L.polygon(farmlandCoordinates, { color: colorMap.farmland }).addTo(map);
            farmlandLayer.bindPopup("Farmland Area");

            farmlandBounds = L.latLngBounds(farmlandCoordinates);

            // Process farm device (npk)
            jsonData.location_coordinates.farm_device.forEach((device) => {
                const [lat, lon] = device.device_location.split(',');
                L.circleMarker([parseFloat(lat), parseFloat(lon)], {
                    radius: 5,
                    fillColor: colorMap.npk,
                    color: colorMap.npk,
                    fillOpacity: 1,
                    stroke: false,
                }).addTo(map).bindPopup(`${device.device_name} (Farm Device)`);
            });

            // Process section devices (moisture sensors and valves)
            jsonData.location_coordinates.section_device.forEach((device) => {
                const [lat, lon] = device.device_location.split(',');
                const coords = [parseFloat(lat), parseFloat(lon)];

                if (device.device_name === "moisture") {
                    // Add moisture sensor markers
                    L.circleMarker(coords, {
                        radius: 6,
                        fillColor: colorMap.moisture,
                        color: colorMap.moisture,
                        fillOpacity: 1,
                        stroke: false,
                    }).addTo(map).bindPopup(`${device.device_name} (${device.section_name})`);
                } else if (device.device_name === "valve") {
                    // Add valve markers
                    L.circleMarker(coords, {
                        radius: 8,
                        fillColor: colorMap.valve,
                        color: colorMap.valve,
                        fillOpacity: 0.6,
                        weight: 2,
                    }).addTo(map).bindPopup(`${device.device_name} (${device.section_name})`);
                }
            });

            // Create legend items dynamically based on unique types
            const legendData = Array.from(uniqueTypes).map((type) => ({
                type,
                color: colorMap[type] || "gray", // Fallback to gray if type doesn't exist in colorMap
            }));
            setLegendItems(legendData);

            // Fit the map to the farmland bounds if available
            if (farmlandBounds) {
                map.fitBounds(farmlandBounds);
            }

            // Cleanup the map when the component unmounts
            return () => {
                map.remove();
            };
        }
    }, [jsonData]); // Depend on jsonData so it updates when new data comes in

    return (
        <div ref={scrollerRef} className="borderring p-0" style={{ overflow: "hidden", scrollbarWidth: "none", msOverflowStyle: "none" }}>
            <div className="borderring" style={{ position: "relative", border: 'none' }}>
                <h6 className="text-secondary" style={{ zIndex: 2, position: "relative", top: '0px', left: '0px' }}>Field Overview</h6>
                <div
                    ref={mapRef}
                    style={{
                        width: "100%",
                        height: "445px",
                        scale: "1.45",
                        overflow: "hidden",
                        position: "relative"
                    }}
                >
                </div>

                {/* Dynamic Legend */}
                <div style={{
                    position: "absolute",
                    top: "10px",
                    right: "10px",
                    backgroundColor: "white",
                    padding: "6px",
                    borderRadius: "5px",
                    boxShadow: "0 0 5px rgba(0,0,0,0.5)",
                    zIndex: 10,
                }}>
                    {legendItems.map((item) => (
                        <div key={item.type} style={{ display: "flex", alignItems: "center" }}>
                            <div style={{ width: "10px", height: "10px", backgroundColor: item.color, marginRight: "5px" }}></div>
                            <span style={{ fontSize: '13px' }}>{item.type.charAt(0).toUpperCase() + item.type.slice(1)}</span>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Map;
