import React from 'react';

const Farmer = ({ farmer_Details }) => {
    const imageStyle = {
        width: "5rem",
        height: "5rem",
        overflow: "hidden",
        borderRadius: "12%",
    };

    const upperCase = (name) => {
        if (name) {
            return name.charAt(0).toUpperCase() + name.slice(1).toLowerCase();
        }
        return '';
    };

    const imgInnerStyle = {
        width: "100%",
        height: "100%",
        objectFit: "cover",
        margin: "0",
    };

    if (!farmer_Details || farmer_Details.length === 0) {
        return <p>No farmer details available.</p>;
    }

    return (
        <div className="user-details d-flex">
            <div className="img border" style={imageStyle}>
                <img
                    src={farmer_Details[0].farmer_image || "../Images/farmer.jpg"}
                    alt="Farmer"
                    style={imgInnerStyle}
                    aria-label="Farmer Profile Image"
                />
            </div>
            <div className="mx-3 d-flex flex-column">
                <h4 className="fw-bold">
                    {upperCase(farmer_Details[0].farmer_fname)}
                </h4>
                <small className="text-secondary">
                    Id : U2024{farmer_Details[0].farmer_id}
                </small>
                <small>
                    Total Fields : <strong className='fs-5 px-1'>{farmer_Details[1]}</strong>
                </small>
            </div>
        </div>
    );
}

export default Farmer;
