import React, { useState, useEffect } from 'react';
import { OverlayTrigger, Popover } from 'react-bootstrap';
import clouds from './WeatherImages/clouds.png';
import clear from './WeatherImages/clear.png';
import drizzle from './WeatherImages/drizzle.png';
import rain from './WeatherImages/rain.png';
import mist from './WeatherImages/mist.png';



const apikey = "8925502a781648f4443f9e01d96c7ff5";
const apiURL = "https://api.openweathermap.org/data/2.5/weather?units=metric&lat=";

const FieldControl = ({ activeDevices }) => {
    const [inputDuration, setInputDuration] = useState('');
    const [isManualMode, setIsManualMode] = useState(false);
    const [isStarted, setIsStarted] = useState(false);
    const [threshold1, setThreshold1] = useState(30);  // Default value
    const [threshold2, setThreshold2] = useState(30);  // Default value
    const [isEditingThreshold1, setIsEditingThreshold1] = useState(false);
    const [isEditingThreshold2, setIsEditingThreshold2] = useState(false);

    // Set thresholds based on activeDevices prop when component mounts or activeDevices changes
    useEffect(() => {
        if (activeDevices.auto_threshold_value && activeDevices.auto_threshold_value.length > 0) {
            const thresholds = activeDevices.auto_threshold_value[0];
            setThreshold1(thresholds.auto_on_threshold || 30);
            setThreshold2(thresholds.auto_off_threshold || 30);
        }
    }, [activeDevices]);

    const handleThresholdChange1 = (e) => {
        setThreshold1(e.target.value);
    };

    const handleThresholdChange2 = (e) => {
        setThreshold2(e.target.value);
    };

    const toggleEditThreshold1 = () => {
        setIsEditingThreshold1(!isEditingThreshold1);
    };

    const toggleEditThreshold2 = () => {
        setIsEditingThreshold2(!isEditingThreshold2);
    };

    const handleManualClick = () => {
        setIsManualMode(true);
        setIsStarted(false); 
        setInputDuration('');
    };

    const handleStartClick = () => {
        if (inputDuration) {
            setIsStarted(true);
        }
    };

    const handleInputChange = (e) => {
        setInputDuration(e.target.value);
        if (!isStarted) {
            setIsStarted(false);
        }
    };

    const handleSaveChanges = (e) => {
        e.preventDefault();
        alert(`Duration saved: ${inputDuration}`);
    };

    const manualPopover = (
        <Popover id="popover-manual">
            <Popover.Body>
                <form onSubmit={handleSaveChanges}>
                    <input
                        className="form-control form-control-sm mt-1 mb-2"
                        type="number"
                        placeholder="Enter duration"
                        value={inputDuration}
                        onChange={handleInputChange}
                    />
                    <div className="d-flex justify-content-evenly align-items-center">
                        <button
                            type="button"
                            className="btn btn-outline-secondary btn-sm w-100 me-3"
                            onClick={handleStartClick}
                            disabled={!inputDuration}
                        >
                            Start
                        </button>
                        <button
                            type="button"
                            className="btn btn-outline-secondary btn-sm w-100"
                        >
                            Stop
                        </button>
                    </div>
                    <div className="text-center my-2">
                        <button
                            type="submit"
                            className="text-center btn btn-outline-secondary btn-sm"
                            disabled={!isStarted}
                        >
                            Save Changes
                        </button>
                    </div>
                </form>
            </Popover.Body>
        </Popover>
    );

    return (
        <div>
            <div className="controller">
                <div className="container">
                    <div className="row pt-2 pb-1">
                        <div className="col-12 col-sm-3 field-control-mobile">
                            <h6 className='text-secondary'>Farm devices</h6>
                            <small>Total Devices : <span className='fw-bold fs-6'>{activeDevices.total_device}</span></small><br />
                            <small>Active Devices : <span className='fw-bold fs-6'>{activeDevices.total_active_devices}</span></small>
                        </div>
                        <div className="col-12 col-sm-3 field-control-mobile">
                            <h6 className="text-secondary m-0">Threshold</h6>
                            <div className="d-flex align-items-center">
                                <small>Auto on threshold : </small>
                                {isEditingThreshold1 ? (
                                    <>
                                        <input
                                            type="number"
                                            value={threshold1}
                                            onChange={handleThresholdChange1}
                                            className="form-control form-control-sm ms-2"
                                            style={{ width: '80px' }}
                                            min={10}
                                            max={30}
                                        />
                                        <button
                                            onClick={toggleEditThreshold1}
                                            className="btn btn-sm btn-outline-secondary ms-2"
                                        >
                                            Save
                                        </button>
                                    </>
                                ) : (
                                    <>
                                        <small>{threshold1}</small>
                                        <button
                                            onClick={toggleEditThreshold1}
                                            className="btn btn-sm ms-1 text-secondary"
                                        >
                                            <i className="fa-solid fa-pen"></i>
                                        </button>
                                    </>
                                )}
                            </div>
                            <div className="d-flex align-items-center">
                                <small>Auto off threshold : </small>
                                {isEditingThreshold2 ? (
                                    <>
                                        <input
                                            type="number"
                                            value={threshold2}
                                            onChange={handleThresholdChange2}
                                            className="form-control form-control-sm ms-2"
                                            style={{ width: '80px' }}
                                        />
                                        <button
                                            onClick={toggleEditThreshold2}
                                            className="btn btn-sm btn-outline-secondary ms-1"
                                        >
                                            Save
                                        </button>
                                    </>
                                ) : (
                                    <>
                                        <small>{threshold2}</small>
                                        <button
                                            onClick={toggleEditThreshold2}
                                            className="btn btn-sm ms-2 text-secondary"
                                        >
                                            <i className="fa-solid fa-pen"></i>
                                        </button>
                                    </>
                                )}
                            </div>
                        </div>
                        <div className='col-12 col-sm-3 field-control-mobile'>
                            <h6 className='text-secondary'>Weather after 6hrs</h6>
                            <div>
                                <span>Cloudy</span>
                                <img 
  src={clouds} 
  width={60} 
  style={{
    filter: 'drop-shadow(6px 5px 5px #0080ff)',
    display: 'block',  // Ensure it's displayed correctly in the layout
    margin: '0 auto'   // Optional: Centers the image horizontally if needed
  }} 
/>

                            </div>
                        </div>
                        <div className="col-12 col-sm-3 toppadding">
                            <h6 className='text-secondary'>Pump controls</h6>
                            <div className="d-flex justify-content-evenly text-center flex-column">
                                <div className='d-flex'>
                                    <button
                                        type="button"
                                        className="btn btn-outline-secondary btn-sm w-100"
                                    >
                                        Auto
                                    </button>
                                    <span className='text-secondary mx-4'>Recommended</span>
                                </div>
                                <OverlayTrigger
                                    trigger="click"
                                    placement="bottom"
                                    overlay={manualPopover}
                                >
                                    <button
                                        type="button"
                                        className="btn btn-outline-secondary btn-sm mt-1"
                                    >
                                        Manual
                                    </button>
                                </OverlayTrigger>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default FieldControl;
