import React from 'react';
import { Modal as BootstrapModal } from 'react-bootstrap';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';

const Modal = ({ show, handleClose, chartData, chartType }) => {
  // Common dataset structure
  const data = {
    labels: chartData.labels,
    datasets: [
      {
        label: chartType === 'timeline' ? 'Timeline Data' : 'Line Chart Data',
        data: chartData.values,
        fill: false,
        backgroundColor: 'rgba(75, 192, 192, 1)',
        borderColor: 'rgba(75, 192, 192, 0.2)',
      },
    ],
  };

  // Customize options for each chart type
  const options = {
    scales: {
      x: chartType === 'timeline' ? {
        type: 'time',
        time: {
          unit: 'day',
        },
      } : {
        beginAtZero: true,
      },
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <BootstrapModal show={show} onHide={handleClose} centered>
      <BootstrapModal.Header closeButton>
        <BootstrapModal.Title>{chartType === 'timeline' ? 'Timeline Chart' : 'Line Chart'}</BootstrapModal.Title>
      </BootstrapModal.Header>
      <BootstrapModal.Body>
        <Line data={data} options={options} />
      </BootstrapModal.Body>
    </BootstrapModal>
  );
};

export default Modal;
