import React, { useState, useEffect } from 'react';
import { OverlayTrigger, Popover } from 'react-bootstrap';
import clouds from './WeatherImages/clouds.png';
import clear from './WeatherImages/clear.png';
import drizzle from './WeatherImages/drizzle.png';
import rain from './WeatherImages/rain.png';
import mist from './WeatherImages/mist.png';

const apikey = "8925502a781648f4443f9e01d96c7ff5";
const apiURL = "https://api.openweathermap.org/data/2.5/forecast?units=metric&lat=";

const FieldControl = ({ collected_data }) => {
  // State declarations
  const [forecastData, setForecastData] = useState(null);
  const [isManualMode, setIsManualMode] = useState(false);
  const [inputDuration, setInputDuration] = useState('');
  const [isStarted, setIsStarted] = useState(false);
  const [threshold1, setThreshold1] = useState(30);
  const [threshold2, setThreshold2] = useState(30);
  const [isEditingThreshold1, setIsEditingThreshold1] = useState(false);
  const [isEditingThreshold2, setIsEditingThreshold2] = useState(false);
  const [recomended, setRecomendation] = useState(false);

  // Destructure fields from collected_data for easier access
  const { device_values, location_coordinates, farm_details } = collected_data || {};
  const moistureDeviceValues = device_values?.moisture_device_value || [];
  const valveDevicesData = device_values?.valve_devices_data || [];
  const predictionLocation = location_coordinates?.farm_device?.[0]?.device_location;
  const { auto_on_threshold, auto_off_threshold } = farm_details || {};

  // Calculate total active moisture devices in the last hour
  const countRecentMoistureValues = (moistureDeviceValues) => {
    const currentTimestamp = new Date();
    const oneHourInMillis = 60 * 60 * 1000;
    return moistureDeviceValues.filter(item => (currentTimestamp - new Date(item.timestamp)) <= oneHourInMillis).length;
  };

  const totalActiveCount = countRecentMoistureValues(moistureDeviceValues);
  const totalDevices = location_coordinates?.section_device?.length + 1 || 0;

  // Extract latitude and longitude from the prediction location
  const [latitude, longitude] = predictionLocation ? predictionLocation.split(", ").map(Number) : [null, null];

  // Update thresholds from farm_details if available
  useEffect(() => {
    setThreshold1(auto_on_threshold || 30);
    setThreshold2(auto_off_threshold || 30);
  }, [auto_on_threshold, auto_off_threshold]);

  // Fetch weather data when latitude and longitude are available
  useEffect(() => {
    if (latitude && longitude) {
      fetch(`${apiURL}${latitude}&lon=${longitude}&appid=${apikey}`)
        .then(response => response.json())
        .then(data => {
          const forecast = data.list[2];
          setForecastData(forecast);
        })
        .catch(error => console.error("Error fetching weather forecast data:", error));
    }
  }, [latitude, longitude]);

  // Recomendation logic based on moisture values
  useEffect(() => {
    if (valveDevicesData.length > 0) {
      const totalMoisture = valveDevicesData.reduce((sum, device) => sum + device.avg_section_moisture, 0);
      const avgMoisture = totalMoisture / valveDevicesData.length;
      const isAnyMoistureLow = valveDevicesData.some(device => device.avg_section_moisture < avgMoisture);
      setRecomendation(isAnyMoistureLow);
    }
  }, [valveDevicesData]);

  // Handle input changes and state toggles
  const handleThresholdChange1 = (e) => setThreshold1(e.target.value);
  const handleThresholdChange2 = (e) => setThreshold2(e.target.value);
  const toggleEditThreshold1 = () => setIsEditingThreshold1(prev => !prev);
  const toggleEditThreshold2 = () => setIsEditingThreshold2(prev => !prev);
  const handleManualClick = () => {
    setIsManualMode(true);
    setIsStarted(false);
    setInputDuration('');
  };
  const handleStartClick = () => inputDuration && setIsStarted(true);
  const handleInputChange = (e) => setInputDuration(e.target.value);
  const handleSaveChanges = (e) => {
    e.preventDefault();
    alert(`Duration saved: ${inputDuration}`);
  };

  // Weather icons mapping
  const weatherIcons = {
    Clouds: clouds,
    Clear: clear,
    Drizzle: drizzle,
    Rain: rain,
    Mist: mist,
  };

  // Utility function to format weather descriptions
  const upperCase = (name) => name.charAt(0).toUpperCase() + name.slice(1).toLowerCase();

  const manualPopover = (
    <Popover id="popover-manual">
      <Popover.Body>
        <form onSubmit={handleSaveChanges}>
          <input
            className="form-control form-control-sm mt-1 mb-2"
            type="number"
            placeholder="Enter duration"
            value={inputDuration}
            onChange={handleInputChange}
          />
          <div className="d-flex justify-content-evenly align-items-center">
            <button
              type="button"
              className="btn btn-outline-secondary btn-sm w-100 me-3"
              onClick={handleStartClick}
              disabled={!inputDuration}
            >
              Start
            </button>
            <button
              type="button"
              className="btn btn-outline-secondary btn-sm w-100"
            >
              Stop
            </button>
          </div>
          <div className="text-center my-2">
            <button
              type="submit"
              className="text-center btn btn-outline-secondary btn-sm"
              disabled={!isStarted}
            >
              Save Changes
            </button>
          </div>
        </form>
      </Popover.Body>
    </Popover>
  );

  return (
    <div>
      <hr className='my-1'></hr>
      <div className="controller">
        <div className="container">
          <div className="row pt-2 pb-1">
            <div className="col-12 col-sm-3 field-control-mobile">
              <h6 className='text-secondary'>Farm devices</h6>
              <small>Total Devices : <span className='fw-bold fs-6'>{totalDevices}</span></small><br />
              <small>Active Devices : <span className='fw-bold fs-6'>{totalActiveCount}</span></small>
            </div>
            <div className="col-12 col-sm-3 field-control-mobile">
              <h6 className="text-secondary m-0">Threshold</h6>
              <div className="d-flex align-items-center">
                <small>Auto on threshold : </small>
                {isEditingThreshold1 ? (
                  <>
                    <input
                      type="number"
                      value={threshold1}
                      onChange={handleThresholdChange1}
                      className="form-control form-control-sm ms-2"
                      style={{ width: '80px' }}
                      min={10}
                      max={30}
                    />
                    <button onClick={toggleEditThreshold1} className="btn btn-sm btn-outline-secondary ms-2">Save</button>
                  </>
                ) : (
                  <>
                    <small>{threshold1}</small>
                    <button onClick={toggleEditThreshold1} className="btn btn-sm ms-1 text-secondary">
                      <i className="fa-solid fa-pen"></i>
                    </button>
                  </>
                )}
              </div>
              <div className="d-flex align-items-center">
                <small>Auto off threshold : </small>
                {isEditingThreshold2 ? (
                  <>
                    <input
                      type="number"
                      value={threshold2}
                      onChange={handleThresholdChange2}
                      className="form-control form-control-sm ms-2"
                      style={{ width: '80px' }}
                    />
                    <button onClick={toggleEditThreshold2} className="btn btn-sm btn-outline-secondary ms-1">Save</button>
                  </>
                ) : (
                  <>
                    <small>{threshold2}</small>
                    <button onClick={toggleEditThreshold2} className="btn btn-sm ms-2 text-secondary">
                      <i className="fa-solid fa-pen"></i>
                    </button>
                  </>
                )}
              </div>
            </div>
            <div className='col-12 col-sm-3 field-control-mobile'>
              <h6 className='text-secondary m-0'>Weather in 6 hrs</h6>
              {forecastData ? (
                <div className='d-flex align-items-center justify-content-between'>
                  <div>
                    <small>{upperCase(forecastData.weather[0].description)}</small><br />
                    <small>{forecastData.main.temp}°C</small>
                  </div>
                  <img
                    src={weatherIcons[forecastData.weather[0].main]}
                    alt={forecastData.weather[0].description}
                    width={65}
                    style={{ filter: 'drop-shadow(0px 0px 5px rgba(0, 0, 0, 0.2))' }}
                  />
                </div>
              ) : (
                <div>Loading weather...</div>
              )}
            </div>
            <div className='col-12 col-sm-3 field-control-mobile'>
              <h6 className='text-secondary'>Manual Mode</h6>
              <button
                className="btn btn-outline-secondary w-100 btn-sm"
                onClick={handleManualClick}
              >
                Manual control
              </button>
              <OverlayTrigger trigger="click" placement="bottom" overlay={manualPopover}>
                <button className="btn btn-outline-secondary w-100 btn-sm mt-1">
                  Set Duration
                </button>
              </OverlayTrigger>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FieldControl;
