import React, { useState } from 'react';
import './User/CSS/User.css';
import 'boxicons/css/boxicons.min.css';
import { useNavigate } from 'react-router-dom';

const LoginSignup = () => {
  const [isActive, setIsActive] = useState(false); // Toggle between User/Admin
  const [formData, setFormData] = useState({ username: '', password: '' });
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (event) => {
    event.preventDefault();
    setLoading(true);
    setError(null);

    const type = isActive ? 'admin' : 'user'; // Determine login type
    const endpoint = isActive ? '/admin/login' : '/login/';

    try {
      const response = await fetch(endpoint, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ user: formData.username, password: formData.password, type }),
      });

      if (!response.ok) {
        const errorMsg = await response.text();
        throw new Error(errorMsg || 'Login failed');
      }

      const data = await response.json();
      console.log(data);
      navigate(`/${type}/${data.userId}`); // Redirect based on user type
    } catch (error) {
      setError(error.message || 'An unexpected error occurred');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  return (
    <div className="bodyLogin">
      <div className={`containeradmin ${isActive ? 'active' : ''}`}>
        {/* Login Form */}
        <div className="form-box login">
          <form onSubmit={handleLogin}>
            <h1>{isActive ? 'Login as Admin' : 'Login as User'}</h1>
            <div className="input-box">
              <input
                type="text"
                name="username"
                placeholder={isActive ? 'Email or ID' : 'Username'}
                value={formData.username}
                onChange={handleInputChange}
                required
              />
              <i className={isActive ? 'bx bxs-envelope' : 'bx bxs-user'}></i>
            </div>
            <div className="input-box">
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleInputChange}
                required
              />
              <i className="bx bxs-lock-alt"></i>
            </div>
            {error && <div className="error-message">{error}</div>}
            <button type="submit" className="BTN" disabled={loading}>
              {loading ? 'Logging in...' : 'Login'}
            </button>
          </form>
        </div>

        {/* Toggle Panels */}
        <div className="toggle-box">
          <div className="toggle-panel toggle-left">
            <h1>Smart Agriculture</h1>
            <button className="BTN register-BTN" onClick={() => setIsActive(true)}>
              Admin
            </button>
          </div>
          <div className="toggle-panel toggle-right">
            <h1>Smart Agriculture</h1>
            <button className="BTN login-BTN" onClick={() => setIsActive(false)}>
              User
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginSignup;
